    private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
        // TODO add your handling code here:

        try {
            tabuada.setText("");
            int vn1 = Integer.parseInt(n1.getText());
            int vn2 = Integer.parseInt(n2.getText());

            for (int i = 0; i < vn2; i++) {

                tabuada.setText(tabuada.getText() + vn1 + " X " + (i + 1) + " = " + vn1 * (i + 1) + "\n");
            }
        } catch (Exception e) {
            n1.setText("");
            n2.setText("");
            tabuada.setText("Formato InvÃ¡lido");

        }


    }//GEN-LAST:event_calcularActionPerformed

