    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        String vChar[] = letras.getText().split(",");
        int tam = vChar.length;
        int total = 0;
        boolean con = true;

        for (int i = 0; i < tam; i++) {
            if (vChar[i].length() > 1) {
                con = false;
            }

            if (vChar[i].equals("a") || vChar[i].equals("A")
                    || vChar[i].equals("e") || vChar[i].equals("E")
                    || vChar[i].equals("i") || vChar[i].equals("I")
                    || vChar[i].equals("o") || vChar[i].equals("O")
                    || vChar[i].equals("u") || vChar[i].equals("U")) {
                total = total + 1;
            }
        }
        if (con) {
            resultado.setText("" + total);
        } else {
            resultado.setText("Verifique os Caracteres");
        }


    }//GEN-LAST:event_jButton1ActionPerformed

