    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        try {
            vetor3.setText("");

            String v1[] = vetor1.getText().split(",");
            String v2[] = vetor2.getText().split(",");

            int tam1 = v1.length;
            int tam2 = v2.length;
            int tam3 = tam1 + tam2;

            int vint1[] = new int[tam1];
            int vint2[] = new int[tam2];
            int vint3[] = new int[tam3];

            for (int i = 0; i < tam1; i++) {
                vint1[i] = Integer.parseInt(v1[i]);
            }
            for (int i = 0; i < tam2; i++) {
                vint2[i] = Integer.parseInt(v2[i]);
            }

            for (int i = 0; i < tam1; i++) {
                vint3[i] = vint1[i];
            }
            for (int i = tam1; i < tam3; i++) {
                vint3[i] = vint2[i - tam1];
            }

            for (int i = 0; i < tam3; i++) {

                vetor3.setText(vetor3.getText() + vint3[i] + "\n");

            }
        } catch (Exception e) {
            
            vetor3.setText("Verifique os Números");
        }


    }//GEN-LAST:event_jButton1ActionPerformed

